import { useState } from 'react';

export default function Upload() {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
    content: []
  });

  const [contentField, setContentField] = useState({
    heading: '',
    text: '',
    image: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('/api/upload', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    });

    if (response.ok) {
      alert('Blog post uploaded successfully!');
    } else {
      alert('Failed to upload blog post.');
    }
  };

  const handleAddContent = () => {
    setFormData({
      ...formData,
      content: [...formData.content, contentField]
    });
    setContentField({ heading: '', text: '', image: '' });
  };

  return (
    <div className="container mx-auto py-8">
      <h1 className="text-2xl font-bold mb-8">Upload New Blog Post</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">Title</label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Description</label>
          <input
            type="text"
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-gray-700">Image URL</label>
          <input
            type="text"
            value={formData.image}
            onChange={(e) => setFormData({ ...formData, image: e.target.value })}
            className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            required
          />
        </div>
        <div className="space-y-4">
          <h2 className="text-lg font-bold">Content Sections</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Heading</label>
            <input
              type="text"
              value={contentField.heading}
              onChange={(e) => setContentField({ ...contentField, heading: e.target.value })}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Text</label>
            <input
              type="text"
              value={contentField.text}
              onChange={(e) => setContentField({ ...contentField, text: e.target.value })}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Image URL</label>
            <input
              type="text"
              value={contentField.image}
              onChange={(e) => setContentField({ ...contentField, image: e.target.value })}
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm"
            />
          </div>
          <button
            type="button"
            onClick={handleAddContent}
            className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-md"
          >
            Add Content Section
          </button>
        </div>
        <button
          type="submit"
          className="mt-4 px-4 py-2 bg-green-500 text-white rounded-md"
        >
          Upload Blog Post
        </button>
      </form>
    </div>
  );
}
